import { Icon } from "@chakra-ui/react";
import { CHAIN_TOKEN_LIST } from "@constants";

export default function AzeroIcon({
  chainToken,
  fill = "#7AE7FF",
  w = "18px",
  h = "18px",
  ml = "12px",
  mb = "6px",
  ...rest
}) {
  switch (chainToken) {
    case CHAIN_TOKEN_LIST.AZERO:
      return alephZeroIcon({ fill, w, h, ml, mb, ...rest });

    case CHAIN_TOKEN_LIST.TZERO:
      return alephZeroIcon({ fill, w, h, ml, mb, ...rest });

    case CHAIN_TOKEN_LIST.ASTR:
      return astarIcon({ w, h, ml, mb, ...rest });

    case CHAIN_TOKEN_LIST.SBY:
      return astarIcon({ w, h, ml, mb, ...rest });

    case CHAIN_TOKEN_LIST.FIVE_IRE:
      return fiveireIcon({ w, h, ml, mb, ...rest });

    default:
      return null;
  }
}

